import React, { useState } from "react";
import axios from "axios";

export default function WeatherSearch(props) {

    let [displayCity, setDisplay] = useState("");
    let [response, setResponse] = useState(null);

    let key = `5f3fe4ffbd1c9babb8c48ec7ebafeb3e`;
    let url = `https://api.openweathermap.org/data/2.5/weather?q=paris&appid=${key}&units=metric`;

    function handleSubmit(event) {


        event.preventDefault();
        setDisplay(`${props.city}`)
        props.setCity(props.city)
        axios.get(url).then(showTemperature);

    }



    function changeCity(event) {
        props.setCity(event.target.value);

    }

    function showTemperature(response) {
        setResponse({

            temperature: response.data.main.temp,
            description: response.data.weather[0].description,
            humidity: response.data.main.humidity,
            wind: response.data.wind.speed,
            icon: response.data.weather[0].icon,
        });
    }

    let form = (

        <form onSubmit={handleSubmit}>
            <input type="search" placeholder="Type a city..." onChange={changeCity} />
            <input type="submit" value="Search" />
        </form>

    );

    return (
        <div>
            {form}
            <h2>{displayCity}</h2>
            <ul>
                <li>Temperature: {weather.temperature}Â°C</li>
                <li>Description: {weather.description}</li>
                <li>Humidity: {weather.humidity}%</li>
                <li>Wind: {weather.wind}km/h</li>
                <li>
                    <img
                        src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`}
                        alt={`${weather.description}`}
                    />
                </li>
            </ul>
        </div>
    );

}
